mapper.select:
select * from (select ROW_NUMBER() OVER (ORDER BY no desc) row_num, tbl.* from notice tbl where 1=1
and title like '%' || #{keyword} || '%'
and contents like '%' || #{keyword} || '%'
) where row_num between #{pageNo} * #{amount}-9 and #{pageNo} * #{amount}

mapper.update:
update notice
set title=#{title}, contents=#{contents}, updated_at=current_date ,build_Code=#{buildCode}
where no=#{no}
